    private void botaoRelatorioDesafiosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoRelatorioDesafiosActionPerformed
        try {
            RelatorioPDF.gerarRelatorioDesafios("relatorio_desafios.pdf", u.getId());
            JOptionPane.showMessageDialog(this, "Relat贸rio gerado com sucesso!");
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(this, "Erro: " + ex.getMessage());
        }

    }//GEN-LAST:event_botaoRelatorioDesafiosActionPerformed

